module.exports = (function () {
    var cart = [];

    function insert(item) {
        cart.push(item);
    };

    function get() {
        return cart;
    };

    function findItem(id) {
        var item = cart.find(x => x.id == id);
    };

    function removeItem(id) {
        cart.splice(cart.findIndex(x => x.id == id), 1);
    };

    return {
        Insert: insert,
        Get: get,
        findItem: findItem,
        removeItem: removeItem
    }
})()